mapper.select:
		select `id`,`user_id`,`name`,`publish_time`,shoucangids,`leave_text`,`audit_status`,`img`,`shoucangcount`,pingluncount,heard_url,shenhe_result,`delete_flag`,`tuijian_flag`,`showhide`,`add_digest`,`top_theme` from leave_message
        where delete_flag=0 and showhide=0
		  		   and id = #{id} 
		  		   and user_id = #{userId} 
		  		   and name = #{name} 
		  		   and publish_time = #{publishTime} 
		  		   and leave_text = #{leaveText} 
		  		   and audit_status = #{auditStatus} 
		  		   and img = #{img} 
				and `publish_time` > #{addStaTime} 
				and `publish_time` < #{addEndTime} 
		  		   and delete_flag = #{deleteFlag} 
		  		   and tuijian_flag = #{tuijianFlag} 
		  		   and showhide = #{showhide} 
		  		   and add_digest = #{addDigest} 
		  		   and top_theme = #{topTheme} 

                order by id desc

select count(*) from leave_message
 where delete_flag=0
  		   and id = #{id} 
  		   and user_id = #{userId} 
  		   and name = #{name} 
  		   and publish_time = #{publishTime} 
  		   and leave_text = #{leaveText} 
  		   and audit_status = #{auditStatus} 
  		   and img = #{img} 
and `publish_time` > #{addStaTime} 
and `publish_time` < #{addEndTime} 
  		   and delete_flag = #{deleteFlag} 
  		   and tuijian_flag = #{tuijianFlag} 
  		   and showhide = #{showhide} 
  		   and add_digest = #{addDigest} 
  		   and top_theme = #{topTheme}
