mapper.select:
		select `id`,`zj_buwei`,video,`zj_name`,xq_title,`zj_gx`,`xuewei_url`,`add_time`,`delete_flag` from t_zjzd
        where delete_flag=0
		  		   and id = #{id} 
		  		   and zj_buwei = #{zjBuwei} 
		  		   and zj_name = #{zjName} 
		  		   and zj_gx = #{zjGx} 
		           and xq_title = #{xqTitle} 
		  		   and xuewei_url = #{xueweiUrl} 
		  		   and add_time = #{addTime} 
		  		   and delete_flag = #{deleteFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

